let iota;
var remote = document.forms["rmtform"]
var replay = document.forms["rplform"]
remote.onsubmit = (e) => {
  remote.hidden = true;
  iota = new IOTA({provider: remote["remote"].value})
  curl.overrideAttachToTangle(iota.api);
  return false
}
replay.onsubmit = (e) => {
  const tail = replay["hash"].value;
  const mwm = replay["mwm"].value;
  const depth = replay["depth"].value;
  console.log(tail, depth, mwm);
  iota.api.replayBundle(tail, 5, mwm, (e, s) => {
    if(e) {
      console.log(e)
    } else {
      console.log(s)
      alert("Re-Attach Completed");
    }
  });
  return false
}
